import './App.css';
import React, { Component } from 'react';

class Game extends Component {
  constructor(props) {
    super(props);
    this.state = {text: "a"}
  }

  increaseScore = () => {
    this.setState({
      score: this.state.text += "a\n" 
    });
  }

  render() {
    return (
      <div className="Game">
        <h2>{this.props.name}</h2>
        <h1>{this.state.score}</h1>
        <button onClick={this.increaseScore}>+2</button>
      </div>
    );
  }
}

function App() {
	return (
    <div id="root">
      <Game />
    </div>
    );
}


export default App;
